$.fn.openModal = function() {
  var $modal = $(this[0]),
    $backdrop = $modal.parents('.js-modal-backdrop'),
    $pageWrapper = $('html');

  $backdrop.fadeIn(300, function() {
    $modal.show();
    $pageWrapper.addClass('no-scroll');
  });
}

$.fn.initModal = function() {
  $(this).each(function() {
    var $modal = $(this),
      $backdrop = $modal.parents('.js-modal-backdrop');

    $modal.hide();

    // $backdrop.on('click', function(event) {
    //   var $el = $(event.target);

    //   if($el.closest($modal).length > 0)
    //     return;

    //   if($backdrop.hasClass('js-no-close'))
    //     return;

    //   $modal.closeModal();
    // })
  })
}

$.fn.closeModal = function() {
  $(this).each(function() {
    var $modal = $(this),
      $backdrop = $modal.parents('.js-modal-backdrop'),
      $pageWrapper = $('html');

    $modal.hide();
    $backdrop.fadeOut(300, function() {
      $pageWrapper.removeClass('no-scroll');
    });

    $(document).trigger('close-modal');
  })
}

